import "./App.css";
import React from "react";

function App() {
	const question = {
		questionText: "일론 머스크의 우주 탐사 기업 이름은?",
		answerOptions: [
			{ answerText: "스페이스 엑스", isCorrect: true },
			{ answerText: "테슬라", isCorrect: false },
			{ answerText: "보링 컴퍼니", isCorrect: false },
			{ answerText: "솔라시티", isCorrect: false },
		],
	};

	const handleAnswer = (isCorrect) => {
		if (isCorrect) {
			alert("정답입니다.");
		}else{
			alert("오답입니다.")
		}
	};
	return (
		<div className="container">
			<div className="app">
				<div className="question-section">
					<h1 className="question-header">
						<span>1</span>/4
					</h1>
					<div className="question-text">{question.questionText}</div>
				</div>
				<div className="answer-section">
					{question.answerOptions.map((answerOption) => (
						<button onClick={() => handleAnswer(answerOption.isCorrect)}>
							{answerOption.answerText}
						</button>
					))}
				</div>
			</div>
		</div>
	);
}

export default App;
